//ids
const JOYSTICK_TYPE = 1
const SPEED_X = 2
const SPEED_Y = 3
const GIMBAL_MODE = 32

//values
const JOYSTICK_WII_TYPE = 1

const SPEED_UNIT = 0.1220740379 //degree/sec


let counter  = context.get('counter')||0;


function cal_checksum(arr){
    let i;
    let Cksm = 0;
    for (i = 3; i < arr.length; i++) { Cksm += arr[i]; }
    let LVZchecksum = [0x00, (Cksm & 0xFF), ((Cksm >> 8) & 0xFF)];

    //add to the counter
    if (counter >= 127) {counter = 0} else {counter = counter + 1}
    context.set('counter',counter);

    return arr.concat(LVZchecksum)
}

function send_mode(mode){
    let message = [0xff, 0xff, 0xff, 101, 1, counter,
                        GIMBAL_MODE, mode, 0]
    let data = cal_checksum(message)

    return Buffer.from(data)
}

function send_speed(x,y){
        x = Number(x)
        y = Number(y)
        if(x == 0 && y == 0){
            return null
        }else{
            let message = [0xff, 0xff, 0xff, 1, 3, counter,
                                    JOYSTICK_TYPE, JOYSTICK_WII_TYPE, 0,
                                    SPEED_X, x & 0xff, x >> 8 & 0xff,
                                    SPEED_Y, y & 0xff, y >> 8 & 0xff]

            let data = cal_checksum(message)

            return Buffer.from(data)
        }
}

let x; 
let y; 
let inverted = global.get("inverted") || undefined;
let speed = global.get("joystick_speed") || 0.025; 
let mode = global.get("joystick_mode") || 0;

if(msg.topic === "mode"){

    mode = msg.payload === 0 ? 0 : 0;
    global.set("joystick_mode", mode);
    msg.payload = send_mode(mode);
    return msg

}else if(msg.inverted){

    global.set("inverted", msg.inverted);

}else if(msg.topic === "joystick_speed"){

    global.set("selected_speed", msg.payload);

    switch(msg.payload) {
        case 1:
            global.set("joystick_speed", 0.025);
            break;
        case 2:
            global.set("joystick_speed", 0.1);
            break;
        case 3:
            global.set("joystick_speed", 0.6);
            break;
        case 4:
            global.set("joystick_speed", 1.2);
            break;
        default:
        // code block
    }
}else{

    x = Math.round(Math.round(Number(msg.payload.vector.x*100)) /SPEED_UNIT) * speed;  //  5 deg/s
    y = Math.round(Math.round(Number(msg.payload.vector.y*100)) /SPEED_UNIT) * speed; //-1 deg/sj
    if(inverted){
        inverted.x ? x = -x : x = x;
        inverted.y ? y = -y : y = y;
    }
    msg.payload = send_speed(x, y);
    if(msg.payload !== null){
        return msg
    }


}

