            4, (controlType.roll & 0xff), ((controlType.roll >> 8) & 0xff), 5,  (controlType.pitch & 0xff), ((controlType.pitch >> 8) & 0xff), 6, (controlType.yaw & 0xff),((controlType.yaw >> 8) & 0xff), 10, (controlType.roll_speed & 0xff), ((controlType.roll_speed >> 8) & 0xFF), 11, (controlType.pitch_speed & 0xff), ((controlType.pitch_speed >> 8) & 0xFF), 12,(controlType.yaw_speed & 0xff), ((controlType.yaw_speed >> 8) & 0xFF),
            13,(controlType.roll_accel & 0xff), ((controlType.roll_accel >> 8) & 0xff), 14 ,(controlType.pitch_accel & 0xff), ((controlType.pitch_accel >> 8) & 0xff), 15 ,(controlType.yaw_accel & 0xff), ((controlType.yaw_accel >> 8) & 0xff), 16, mode, 0] 
    }
    else if(controlType && msg.topic === "change_network"){
        var boxid = Number(controlType.boxid);
