    }
    //calculate checksum
    let i;
    let Cksm = 0;
    for (i = 3; i < message.length; i++) { Cksm = (Cksm + message[i]) & 0xFFFF ; }
    let LVZchecksum = [0x00, (Cksm & 0xFF), ((Cksm >> 8) & 0xFF)];
    flow.set("checksum", LVZchecksum)

    //the complete LVZ message
    let LVZmsg    = message.concat(LVZchecksum);


    return Buffer.from(LVZmsg)

}


