data.forEach(myFunction);
function myFunction(value, index, array) {

    // detect starting sequence
  if(value === 255 && array[index+1] === 255 && array[index+2] === 255 ){
    // store ids and values
    let id_counter = 6;
    let data2 = [];
    while(array[index+id_counter] > 0){
        let id_and_values = [array[index+id_counter],(array[index+(id_counter+1)] + array[index+(id_counter+2)] * 256)];
        data2.push(id_and_values);
        id_counter += 3;
    }
    // calculate checksum
    if(array[index+id_counter] === 0){
      // checksum
      let calculated_checksum = 0;
      let message = array.slice(index+3, index+(id_counter));
      let checksum = array[index+(id_counter+1)] + array[index+(id_counter+2)] *256; // checksum is on the last 2 bytes
      for (let i = 0; i < message.length; i++)  {calculated_checksum += message[i]; }

      if(checksum == calculated_checksum ){
        device_id = array[index+3];
        device_type = array[index+4];
        counterAndType = array[index+5];
        let data_id;
        let value;
        for(let i = 0; i < data2.length; i++){
            data_id = data2[i][0];
            value = data2[i][1]
            displayData(device_id, data_id, device_type , value);
        }
      }
    }
  }   
}

function displayData(device_id, data_id, device_type, value){
    //gimbal
    if(device_type == GIMBAL_TYPE){
        if(data_id == YAW_ANGLE){
            response.push({yaw: Number(value*ANGLE_UNIT)})
        }
        else if(data_id == PITCH_ANGLE){
            response.push({pitch: Number(value*ANGLE_UNIT)})
        }    
        else if(data_id == ROLL_ANGLE){
            response.push({roll: Number(value*ANGLE_UNIT)})
        }    
        else if(data_id == TIMESTAMP){
            response.push({timestamp: Date(value * 1000)})
        }
        else if(data_id == 1){

            let newRollValue = (Number(value*ANGLE_UNIT) > 360) ? ((-1440) + Number(value*ANGLE_UNIT)) : Number(value*ANGLE_UNIT);
            global.set("IMU_roll", Number(newRollValue).toFixed(2));
            global.set("roll", Number(newRollValue).toFixed(2) / ANGLE_UNIT);
            response.push({IMU_roll: Number(newRollValue)})
        }    
        else if(data_id == 2){
            let newPitchValue = (Number(value*ANGLE_UNIT) > 360) ? ((-1440) + Number(value*ANGLE_UNIT)) : Number(value*ANGLE_UNIT);
            global.set("IMU_pitch", Number(newPitchValue).toFixed(2));
            global.set("pitch", Number(newPitchValue).toFixed(2) / ANGLE_UNIT);
            response.push({IMU_pitch: Number(newPitchValue)})

        }    
        else if(data_id == 3){
            let newYawValue = (Number(value*ANGLE_UNIT) > 360) ? ((-1440) + Number(value*ANGLE_UNIT)) : Number(value*ANGLE_UNIT);
            global.set("IMU_yaw", Number(newYawValue).toFixed(2));
            global.set("yaw", Number(newYawValue).toFixed(2) / ANGLE_UNIT);
            response.push({IMU_yaw: Number(newYawValue)})
        }else if(data_id == 44){
            motor = true;
        }
    }

    // box
    if(device_type == BOX_TYPE){
        if(data_id == 30 || data_id == 31 || data_id == 32 || data_id == 33){
            if(data_id == 33 && ip_counter === 0){
                ip_counter = 1;
                networkInfo.ip +=`${value}`;
            }else if (ip_counter === 0){
                networkInfo.ip +=`${value}.`; 
            }
        } else if (data_id == 49 || data_id == 50 || data_id == 51 || data_id == 52 || data_id == 53){
           if(data_id == 53 && mac_counter === 0){
                mac_counter = 1;
                networkInfo.mac +=`${value}`;
            }else if (mac_counter === 0){
                networkInfo.mac +=`${value}:`; 
            } 
        }
        else if(data_id == 38 || data_id == 39 || data_id == 40 || data_id == 41){
            if(data_id == 41 && mask_counter === 0){
                mask_counter = 1;
                networkInfo.mask +=`${value}`;
            }else if (mask_counter === 0){
                networkInfo.mask +=`${value}.`; 
            }
        }
        else if(data_id == 46 || data_id == 47){
            if(data_id == 47 && port_counter === 0){
                port_counter = 1;
                networkInfo.udp =`${value}`;
            }else if (port_counter === 0){
                networkInfo.tcp =`${value}`; 
            }
        }
        else if(data_id == 48){
            networkInfo.Nmode =`${value}`;
        }else if(data_id == 55){
            networkInfo.gimbalid =`${value}`;
        }else if(data_id == 56){
            networkInfo.boxid =`${value}`;
        }else if(data_id == 7){
            version.v1 = value;
        }else if(data_id == 8){
            version.v2 = value;
        }else if(data_id == 9){
            version.v3 = value;
        }

    }

}

//sending data 
if(device_type == GIMBAL_TYPE && response.length > 0){
    msg.topic = "angle_values";
