function type10mapping(IN) {



switch(IN.Control) {

  case "focus":        LVZ_ID_Data = buildLVZsingle    (1  , IN.DATA); save.focus       = IN.DATA;  break;

  case "fstop":        LVZ_ID_Data = buildLVZsingle    (3  , IN.DATA); save.fstop       = IN.DATA;  break;

  case "gain":         LVZ_ID_Data = buildLVZsingle    (31 , IN.DATA); save.gain        = IN.DATA;  break;

  case "exposure":     LVZ_ID_Data = buildLVZsingleLong(35 , IN.DATA); save.exposure    = IN.DATA;  break;

  case "expMod":       LVZ_ID_Data = buildLVZsingleLong(40 , IN.DATA); save.expMod      = IN.DATA;  break;

  case "tallyMod":     LVZ_ID_Data = buildLVZsingleLong(113, IN.DATA); save.tallyMod    = IN.DATA;  break;

  case "tallyBright":  LVZ_ID_Data = buildLVZsingleLong(110, IN.DATA); save.tallyBright = IN.DATA;  break;

  case "WB":           LVZ_ID_Data = buildLVZdual      (32 , IN.DATA1, IN.DATA2); 
                                                  save.WB = [IN.DATA1, IN.DATA2 ];                  break;

  case "liftAdjust":   LVZ_ID_Data = buildLVZliftAdjust(IN);     
  save.liftAdjust =  { Red: IN.Red, Green: IN.Green,  Blue: IN.Blue, Luma: IN.Luma,  Colour: IN.Colour } ;    break;


  case "gammaAdjust":  LVZ_ID_Data = buildLVZgammaAdjust(IN.COLOR);   save.gammaAdjust = IN.COLOR;  break;


  case "gainAdjust":   LVZ_ID_Data = buildLVZgainAdjust(IN);     
  save.gainAdjust =  { Red: IN.Red, Green: IN.Green,  Blue: IN.Blue, Luma: IN.Luma,  Colour: IN.Colour } ;    break;



  case "offsetAdjust": LVZ_ID_Data = buildLVZoffsetAdjust(IN.COLOR); save.offsetAdjust = IN.COLOR;  break; 

 // case "resetColour":  LVZ_ID_Data = resetColours();                                                break;

 // case "SendData":     sendType10(saveID);break;


  default:
    LVZ_ID_data = [ ];
}

flow.set(saveID,save);
return LVZ_ID_Data;
}

