function buildLVZgammaAdjust(GMAcontrol){


//64 gives ful +-4.0 range, often less is enough and gives beter adjustability 
var multc = 4;//colour multiblier
var multl = 8;//luma multiblier

//the LVZ message data
//var DataIn  = msg.payload;
var red   = parseInt("0x"+GMAcontrol.slice(0, 2));
var green = parseInt("0x"+GMAcontrol.slice(2, 4));
var blue  = parseInt("0x"+GMAcontrol.slice(4, 6));
var luma  = parseInt("0x"+GMAcontrol.slice(6, 8));


red   = (multc * (red   - 127 ))||0;
green = (multc * (green - 127 ))||0;
blue  = (multc * (blue  - 127 ))||0;
luma  = (multl * (127   - luma))||0;

var LVZ_ID_data = buildLVZcolour(141, red, green, blue, luma);

return LVZ_ID_data;
}


function buildLVZgainAdjust(Din){

//the LVZ message data

var red   = Din.Red;
