luma  = (multl * (127   - luma))||0;

var LVZ_ID_data = buildLVZcolour(143, red, green, blue, luma);

return LVZ_ID_data;
}



function buildLVZmsg(Type, Camera, LVZ_ID_data) {


var Counter  = context.get('Counter')||0;
if (Counter >= 127) {Counter = 0;} else {Counter++;}
context.set('Counter',Counter);

var LVZheader   = [0xFF, 0xFF, 0xFF,Camera ,Type ,Counter ];

//combine header and data
var LVZdata  = LVZheader.concat(LVZ_ID_data);

//calculate checksum
var i;
var Cksm = 0;
for (i = 3; i < LVZdata.length; i++) { Cksm = (Cksm + LVZdata[i])& 0xFFFF; }
var LVZchecksum = [0x00, (Cksm & 0xFF), ((Cksm >> 8) & 0xFF)];

//the complete LVZ message
var LVZmsg    = LVZdata.concat(LVZchecksum);

return Buffer.from(LVZmsg);

}


function buildLVZsingle(LVZ_ID, Data) {

var DataL   =  (Data &       0xFF)||0;
var DataH   = ((Data >> 8) & 0xFF)||0;

var LVZ_ID_data = [ LVZ_ID , DataL, DataH];

return LVZ_ID_data;

}

function buildLVZsingleLong(LVZ_ID, Data) {

var Data0   =  (Data &        0xFF)||0;
var Data1   = ((Data >> 8)  & 0xFF)||0;
var Data2   = ((Data >> 16) & 0xFF)||0;
var Data3   = ((Data >> 24) & 0xFF)||0;

var LVZ_ID_data = [ LVZ_ID , Data0, Data1, 254, Data2, Data3];

return LVZ_ID_data;

}

function buildLVZdual(LVZ_ID, Data_1, Data_2) {

var Data0   =  (Data_1 &        0xFF)||0;
var Data1   = ((Data_1 >> 8)  & 0xFF)||0;
var Data2   =  (Data_2 &        0xFF)||0;
var Data3   = ((Data_2 >> 8)  & 0xFF)||0;

var LVZ_ID_data = [ LVZ_ID , Data0, Data1, 254, Data2, Data3];

return LVZ_ID_data;

}

function buildLVZcolour(LVZ_ID, red, green, blue, luma) {

var redH   = ((red   >> 8) & 0xFF)||0;
var redL   =  (red   &       0xFF)||0;
var greenH = ((green >> 8) & 0xFF)||0;
var greenL =  (green &       0xFF)||0;
var blueH  = ((blue  >> 8) & 0xFF)||0;
var blueL  =  (blue  &       0xFF)||0;
var lumaH  = ((luma  >> 8) & 0xFF)||0;
var lumaL  =  (luma  &       0xFF)||0;

var LVZ_ID_data = [ LVZ_ID , redL, redH, 254, greenL, greenH, 254, blueL, blueH, 254, lumaL, lumaH];

return LVZ_ID_data;

}

