function toFstop(Fstop){

 //BMMSC available WB values    
var FstopValues   = [1550, 1988, 2396, 3136, 3473, 4096, 4659, 5173, 5646, 6084, 6873, 7403, 7731, 8192, 8888, 9269, 9742, 10180, 10782, 11240, 11747, 12288, 12784, 13304, 13607, 14170, 14684, 15595, 16003, 16384];

// Sppedbooster + Canon 50 1.4 SMT: 1.3; 1.4; 1.5; 1.7; 1.8; 2.0; 2.2; 2.4; 2.6; 2.8; 3.2; 3.5; 3.7; 4.0; 4.5; 4.8; 5.2; 5.6; 6.2; 6.7; 7.3; 8.0; 8.7; 9.5; 10; 11; 12; 14; 15; 16 
// DataOut = (Math. sqrt(Math.pow(2, (msg.payload.DATA/2048)))).toFixed(1);

//find closest available WB to current slider position
var idx = 0; 
var distance = 1000000;

 var i;
  for ( i = 0; i < FstopValues.length; i++)
  {
    var d = Math.abs(FstopValues[i] - Fstop);
    if (d < distance)
    {
      idx = i;
      distance = d;
    }
  }
  return FstopValues[idx];

}

