function toWB(WBin){

 //BMMSC available WB values    
 var WBvalues   = [2500, 2800, 3000, 3200, 3400, 3600, 4000, 4500, 4800, 5000, 5200, 5400, 5600, 6000, 6500, 7000, 7500, 8000 ];   



//find closest available WB to current slider position
var idx = 0; 
var distance = 1000;

 var i;
  for ( i = 0; i < WBvalues.length; i++)
  {
    var d = Math.abs(WBvalues[i] - WBin);
    if (d < distance)
    {
      idx = i;
      distance = d;
    }
  }
  return WBvalues[idx];

}

