var yaw = global.get("yaw") || 0;
var pitch = global.get("pitch") || 0;
var roll = global.get("roll") || 0;
var yaw_speed = global.get("yaw_speed") !== undefined ? global.get("yaw_speed") : 200;
var pitch_speed = global.get("pitch_speed") !== undefined ? global.get("pitch_speed") : 200;
var roll_speed = global.get("roll_speed") !== undefined ? global.get("roll_speed") : 200;
var yaw_accel = global.get("yaw_accel") !== undefined ? global.get("yaw_accel") : 500;
var pitch_accel = global.get("pitch_accel") !== undefined ? global.get("pitch_accel") : 500;
var roll_accel = global.get("roll_accel") !== undefined ? global.get("roll_accel") : 500;
var name = global.get("selected") || undefined;
var array = global.get("preset_array") || undefined;

if(name && array){
    var preset = {
        name: name,
        yaw: yaw,
        pitch: pitch,
        roll: roll,
        yaw_speed: yaw_speed,
        pitch_speed: pitch_speed,
        roll_speed: roll_speed,
        yaw_accel: yaw_accel,
        pitch_accel: pitch_accel,
        roll_accel: roll_accel
    }
    var presets = JSON.parse(array);
    var updated = presets.map(o => o.name !== name ? o : preset);
    global.set("preset_array", JSON.stringify(updated));
    msg.payload = updated;
    msg.topic = "store";
    msg.notify = name;
    return msg;
}

