var distance = msg.multi;
var currentSpeed = global.get("joystick_speed") || 0.025;
var selectedSpeed = global.get("selected_speed") || 1;
var max = 4.8;
var divider = 2.5;

function setSelectedSpeed(speed, times){
   switch(speed) {
        case 1:
            global.set("joystick_speed", Number((0.025 * (times ? (times/divider) : 1)).toFixed(3)) );
            break;
        case 2:
            global.set("joystick_speed", Number((0.1 * (times ? (times/divider) : 1)).toFixed(3)) );
            break;
        case 3:
            global.set("joystick_speed", Number((0.6 * (times ? (times/divider) : 1)).toFixed(3)) );
            break;
        case 4:
            global.set("joystick_speed", Number((1.2 * (times ? (times/divider) : 1)).toFixed(3)) );
            break;
        default:
            break;
    } 
}

if(currentSpeed < max && distance > 0 && distance <= 10){
    switch(distance) {
        case 1: case 2: 
            setSelectedSpeed(selectedSpeed, distance);
            break;
        case 3: case 4:
            setSelectedSpeed(selectedSpeed, distance);
            break;
        case 5: case 6:
            setSelectedSpeed(selectedSpeed, distance);
            break;
        case 7: case 8:
            setSelectedSpeed(selectedSpeed, distance);
            break;
        default:
            setSelectedSpeed(selectedSpeed, distance);
            break;
    }

}else if(currentSpeed < max && distance < 1){
    setSelectedSpeed(selectedSpeed);
}
msg.payload = currentSpeed;
return msg;

